parameters.functionCode:
// Validate incoming invoice data
const invoiceData = $input.first().json;

// Required field validation
const requiredFields = ['invoice_id', 'vendor_id', 'total_amount', 'invoice_date', 'due_date'];
const missingFields = requiredFields.filter(field => !invoiceData[field]);

if (missingFields.length > 0) {
  return [{
    json: {
      status: 'error',
      error: `Missing required fields: ${missingFields.join(', ')}`,
      invoice_id: invoiceData.invoice_id
    }
  }];
}

// Amount validation
if (invoiceData.total_amount <= 0) {
  return [{
    json: {
      status: 'error',
      error: 'Total amount must be greater than 0',
      invoice_id: invoiceData.invoice_id
    }
  }];
}

// Date validation
const invoiceDate = new Date(invoiceData.invoice_date);
const dueDate = new Date(invoiceData.due_date);

if (dueDate <= invoiceDate) {
  return [{
    json: {
      status: 'error',
      error: 'Due date must be after invoice date',
      invoice_id: invoiceData.invoice_id
    }
  }];
}

// If all validations pass
return [{
  json: {
    status: 'validated',
    invoice_id: invoiceData.invoice_id,
    validation_summary: {
      total_amount: invoiceData.total_amount,
      vendor_id: invoiceData.vendor_id,
      days_until_due: Math.ceil((dueDate - new Date()) / (1000 * 60 * 60 * 24))
    }
  }
}];

// Check for duplicate invoices
const validationData = $input.first().json;
const invoiceId = validationData.invoice_id;

// In a real implementation, you would check against your database
// For this example, we'll simulate the check
const duplicateFound = Math.random() < 0.1; // 10% chance of duplicate

if (duplicateFound) {
  return [{
    json: {
      status: 'duplicate_found',
      invoice_id: invoiceId,
      action: 'review_required',
      message: 'Duplicate invoice detected - manual review required'
    }
  }];
}

return [{
  json: {
    status: 'no_duplicate',
    invoice_id: invoiceId,
    action: 'continue_processing'
  }
}];

// Auto-approval logic for invoices under threshold
const invoiceData = $input.first().json;

return [{
  json: {
    status: 'auto_approved',
    invoice_id: invoiceData.invoice_id,
    approval_details: {
      approved_by: 'system',
      approved_at: new Date().toISOString(),
      approval_reason: 'Amount below auto-approval threshold'
    }
  }
}];

// Prepare data for ERP export
const approvedData = $input.first().json;

const erpExport = {
  invoice_id: approvedData.invoice_id,
  vendor_id: approvedData.validation_summary?.vendor_id || 'unknown',
  total_amount: approvedData.validation_summary?.total_amount || 0,
  currency: 'USD',
  due_date: new Date().toISOString(),
  approval_status: approvedData.status,
  export_timestamp: new Date().toISOString(),
  export_version: '1.0'
};

return [{
  json: {
    status: 'export_prepared',
    invoice_id: approvedData.invoice_id,
    erp_export_data: erpExport
  }
}];

